              continue;
            }
            const info = this._getNavigationInfoFromConfig(page);
            if (!info) {
              continue;
            }
            // Add to list, but only if we do not already have an entry for the same path and component
            if (items.some(e => e.path === info.path)) {
              continue;
            }
            items.push(info);
          }
        }
        return items;
      }
    }, {
      kind: "method",
      key: "_getNavigationInfoFromConfig",
      value: function _getNavigationInfoFromConfig(page) {
        const path = page.path.substring(1);
        let name = path.substring(path.indexOf("/") + 1);
        name = name.indexOf("/") > -1 ? name.substring(0, name.indexOf("/")) : name;
        const caption = name && this.hass.localize(`ui.dialogs.quick-bar.commands.navigation.${name}`) ||
        // @ts-expect-error
        page.translationKey && this.hass.localize(page.translationKey);
        if (caption) {
          return {
            ...page,
            primaryText: caption
          };
        }
        return undefined;
      }
    }, {
      kind: "method",
      key: "_finalizeNavigationCommands",
      value: function _finalizeNavigationCommands(items) {
        return items.map(item => {
          const categoryKey = "navigation";
          const navItem = {
            ...item,
            iconPath: mdiEarth,
            categoryText: this.hass.localize(`ui.dialogs.quick-bar.commands.types.${categoryKey}`),
            action: () => navigate(item.path)
          };
          return {
            ...navItem,
            strings: [`${navItem.categoryText} ${navItem.primaryText}`],
            categoryKey
          };
        });
      }
    }, {
      kind: "method",
      key: "_toggleIfAlreadyOpened",
      value: function _toggleIfAlreadyOpened() {
        return this._opened ? !this._commandMode : false;
      }
    }, {
      kind: "field",
      key: "_filterItems",
      value() {
        return memoizeOne((items, filter) => fuzzyFilterSort(filter.trimLeft(), items));
      }
    }, {
      kind: "get",
      static: true,
      key: "styles",
      value: function styles() {
        return [haStyleScrollbar, haStyleDialog, css`
        mwc-list {
          position: relative;
          --mdc-list-vertical-padding: 0;
        }
        .heading {
          display: flex;
          align-items: center;
          --mdc-theme-primary: var(--primary-text-color);
        }

        .heading ha-textfield {
          flex-grow: 1;
        }

        ha-dialog {
          --dialog-z-index: 9;
          --dialog-content-padding: 0;
        }

        @media (min-width: 800px) {
          ha-dialog {
            --mdc-dialog-max-width: 800px;
            --mdc-dialog-min-width: 500px;
            --dialog-surface-position: fixed;
            --dialog-surface-top: 40px;
            --mdc-dialog-max-height: calc(100% - 72px);
          }
        }

        @media all and (max-width: 450px), all and (max-height: 500px) {
          ha-textfield {
            --mdc-shape-small: 0;
          }
        }

        @media all and (max-width: 450px), all and (max-height: 690px) {
          .hint {
            display: none;
          }
        }

        ha-svg-icon.prefix {
          color: var(--primary-text-color);
        }

        ha-textfield ha-icon-button {
          --mdc-icon-button-size: 24px;
          color: var(--primary-text-color);
        }

        .command-category {
          --ha-label-icon-color: #585858;
          --ha-label-text-color: #212121;
        }

        .command-category.reload {
          --ha-label-background-color: #cddc39;
        }

        .command-category.navigation {
          --ha-label-background-color: var(--light-primary-color);
        }

        .command-category.server_control {
          --ha-label-background-color: var(--warning-color);
        }

        span.command-text {
          margin-left: 8px;
          margin-inline-start: 8px;
          margin-inline-end: initial;
          direction: var(--direction);
        }

        ha-list-item {
          width: 100%;
          --mdc-list-item-graphic-margin: 20px;
        }

        ha-list-item.command-item {
          text-transform: capitalize;
        }

        ha-tip {
          padding: 20px;
        }

        .nothing-found {
          padding: 16px 0px;
          text-align: center;
        }

        div[slot="trailingIcon"] {
          display: flex;
          align-items: center;
        }

        lit-virtualizer {
          contain: size layout !important;
        }
      `];
      }
    }]
  };
